mapper.select:
select count(1)
from user_like
where user_id = #{userId}
  and donor_id = #{donorId}
  and manuscript_id = #{manuscriptId}
  and delete_mark = 0

mapper.update:
update user_like

        update_time = sysdate(),

        delete_mark = #{deleteMark},


where user_id = #{userId}
and donor_id = #{donorId}
and manuscript_id = #{manuscriptId}
